import React, { Component } from "react";
import { useEffect, useState } from "react";
import SimpleStorageContract from "./contracts/SimpleStorage.json";
import CarSharingContract from "./contracts/CarSharing.json";
import getWeb3 from "./getWeb3";

import "./App.css";

//State variables
const [walletAddress, setWallet] = useState("");
const [status, setStatus] = useState("");
const [message, setMessage] = useState("No connection to the network.");
const [newMessage, setNewMessage] = useState("");

//called only once
useEffect(async () => {
  const message = await loadCurrentMessage();
  setMessage(message);
  addSmartContractListener();
  const {address, status} = await getCurrentWalletConnected();
  setWallet(address)
  setStatus(status); 
  addWalletListener(); 
}, []);
function addSmartContractListener() {
  helloWorldContract.events.UpdatedMessages({}, (error, data) => {
    if (error) {
      setStatus("ğŸ˜¥ " + error.message);
    } else {
      setMessage(data.returnValues[1]);
      setNewMessage("");
      setStatus("ğŸ‰ Your message has been updated!");
    }
  });
}
function addWalletListener() {
  if (window.ethereum) {
    window.ethereum.on("accountsChanged", (accounts) => {
      if (accounts.length > 0) {
        setWallet(accounts[0]);
        setStatus("ğŸ‘†ğŸ½ Write a message in the text-field above.");
      } else {
        setWallet("");
        setStatus("ğŸ¦Š Connect to Metamask using the top right button.");
      }
    });
  } else {
    setStatus(
      <p>
        {" "}
        ğŸ¦Š{" "}
        <a target="_blank" href={`https://metamask.io/download.html`}>
          You must install Metamask, a virtual Ethereum wallet, in your
          browser.
        </a>
      </p>
    );
  }
}
class App extends Component {

  state = { message: null, userName: null, userId: null, rentStatus: null, web3: null, accounts: null, contract: null };
  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      // const networkId = await web3.eth.net.getId();
      // const deployedNetwork = SimpleStorageContract.networks[networkId];
      // const instance = new web3.eth.Contract(
      //   SimpleStorageContract.abi,
      //   deployedNetwork && deployedNetwork.address,
      // );

      const networkId = await web3.eth.net.getId();
      const deployedNetwork = CarSharingContract.networks[networkId];
      const instance = new web3.eth.Contract(
        CarSharingContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.startApp);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };


  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.set(5).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    const response = await contract.methods.get().call();

    // Update state with the result.
    this.setState({ storageValue: response });
  };

  testWrite = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.writeMessage("test").send({from: accounts[0]});


  };

  startApp = async () => {
    const { accounts, contract } = this.state;
    // Stores a given value
   //await contract.methods.rentCar(10).send({ from: accounts[0] });
   let response = await contract.methods.getRegistrationStatus(accounts[0]).call();
   if(response == true){
     this.setState({ registrationStatus: "You are registered"});
   }
   // Get the value from the contract to prove it worked.
  //  const response = await contract.methods.getRentStatus(accounts[1]).call();
  //   if(response == true){
  //     this.setState({ rentStatus: "A car has been assigned to you" });
  //   }else{
  //     this.setState({ rentStatus: "No car is assigned to you" });
  //   }

  //  const response2 = await contract.methods.getRegistrationStatus(accounts[1]).call();

  //   if(response2 == true){
  //     this.setState({ registrationStatus: "You are registered" });
  //   }else{
  //     this.setState({ registrationStatus: "You are not registered" });
  //   }

 };

 rentCar = async () => {
  const { accounts, contract } = this.state;
  // Stores a given value


 // Get the value from the contract to prove it worked.
 const response = await contract.methods.rentCar(10).call();
  if(response == 1){
    this.setState({ rentStatus: "A car has been assigned to you" });
  }else{
    this.setState({ rentStatus: "No car is assigned to you" });
  }

};

registerUser = async () => {
  const { accounts, contract } = this.state;
  // Stores a given value


 // Get the value from the contract to prove it worked.
 try{
  await contract.methods.registerDriver(this.state.userName, this.state.userId).send({ from: accounts[0] });

  //truffleAssert.eventEmitted(result, 'New)
  let response = await contract.methods.getRegistrationStatus(accounts[0]).call();
  if(response == true){
    this.setState({ registrationStatus: "You are registered"});
  }
 }catch(err){
  //let message = JSON.parse(err.message.substring(56).trim().replace("'", "")).value.data.data;
  //console.log(message[Object.keys(message)[0]].reason);
   this.setState({ message: "test"});
 }

};

  setStorage = async () => {

    // Stores a given value
   await this.state.contract.methods.set(3).send({ from: this.state.accounts });

   // Get the value from the contract to prove it worked.
   const response = await this.state.contract.methods.get().call();

   // Update state with the result.
   return this.setState({ storageValue: response });
 };

 inputChanged = (e) => {
  this.setState({ inputVal: e.target.value });
}

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Welcome to SG Car Sharing Service!</h1>
        <label>{this.state.message}</label>
        <label>Name: </label>
        <input onChange={event => this.setState({ userName: event.target.value })}/>
        <label>ID: </label>
        <input onChange={event => this.setState({ userId: event.target.value })}/>
        <button onClick={this.registerUser}>Register</button>
          <div>Registration Status: {this.state.registrationStatus}</div>
          <div>Rent Status: {this.state.rentStatus}</div>
          <button onClick={this.rentCar}>Rent Car</button>




      </div>
    );
  }
}

export default App;
