import React, { Component } from "react";
import { useEffect, useState } from "react";
import CarSharingContract from "./contracts/CarSharing.json";
import getWeb3 from "./getWeb3";

import "./App.css";

const HelloWorld = () => {
  //state variables
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("No connection to the network."); //default message
  const [newMessage, setNewMessage] = useState("");
  const carContract = useState("");

  const componentDidMount = async () => {
    try {
      alert(
        `ailed to load Fweb3, accounts, or contract. Check console for details.`,
      );
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      // const networkId = await web3.eth.net.getId();
      // const deployedNetwork = SimpleStorageContract.networks[networkId];
      // const instance = new web3.eth.Contract(
      //   SimpleStorageContract.abi,
      //   deployedNetwork && deployedNetwork.address,
      // );

      const networkId = await web3.eth.net.getId();
      const deployedNetwork = CarSharingContract.networks[networkId];
      const instance = new web3.eth.Contract(
        CarSharingContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      instance.events.UserRegistered({}, (error, data) => {
        if (error) {
          setStatus("ğŸ˜¥ " + error.message);
        } else {
          setMessage(data.returnValues[1]);
          setNewMessage("");
          setStatus("ğŸ‰ Your message has been updated!");
        }
      });

      const { address, status } = await getCurrentWalletConnected();

      setWallet(address);
      setStatus(status);

      addWalletListener();
      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      //this.setState({ web3, accounts, contract: instance }, this.startApp);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };


  /* //called only once
  useEffect(async () => {
    const web3 = await getWeb3();
    const contractABI = CarSharingContract;
    const contractAddress = "0xb7B7459DAF2B61bBb32Ba5Ab8dDfF858cd7B5332";
    carContract = new web3.eth.Contract(
      contractABI,
      contractAddress
    );

    // const message = await getRegistrationStatus();
    // setMessage(message);
    addSmartContractListener();

    const { address, status } = await getCurrentWalletConnected();

    setWallet(address);
    setStatus(status);

    addWalletListener();
  }, []); */

  function addSmartContractListener() {
    carContract.events.Received({}, (error, data) => {
      if (error) {
        setStatus("ğŸ˜¥ " + error.message);
      } else {
        setMessage(data.returnValues[1]);
        setNewMessage("");
        setStatus("ğŸ‰ Your message has been updated!");
      }
    });
  }

  function addWalletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
          setStatus("ğŸ‘†ğŸ½ Write a message in the text-field above.");
        } else {
          setWallet("");
          setStatus("ğŸ¦Š Connect to Metamask using the top right button.");
        }
      });
    } else {
      setStatus(
        <p>
          {" "}
          ğŸ¦Š{" "}
          <a target="_blank" href={`https://metamask.io/download.html`}>
            You must install Metamask, a virtual Ethereum wallet, in your
            browser.
          </a>
        </p>
      );
    }
  }

  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };


  // interactions

 const loadRegistrationStatus = async () => {
  const status = await CarSharingContract.methods.getRegistrationStatus(walletAddress).call();
  return status;
};


 const registerUser = async () => {
  await CarSharingContract.methods.registerDriver(this.state.userName, this.state.userId).send({ from: walletAddress });

  let response = await CarSharingContract.methods.getRegistrationStatus(walletAddress).call();
  if(response == true){
    this.setState({ registrationStatus: "You are registered"});
  }

};

 const connectWallet = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const obj = {
        status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
        address: addressArray[0],
      };
      return obj;
    } catch (err) {
      return {
        address: "",
        status: "ğŸ˜¥ " + err.message,
      };
    }
  } else {
    return {
      address: "",
      status: (
        <span>
          <p>
            {" "}
            ğŸ¦Š{" "}
            <a target="_blank" href={`https://metamask.io/download.html`}>
              You must install Metamask, a virtual Ethereum wallet, in your
              browser.
            </a>
          </p>
        </span>
      ),
    };
  }
};

 const getCurrentWalletConnected = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_accounts",
      });
      if (addressArray.length > 0) {
        return {
          address: addressArray[0],
          status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
        };
      } else {
        return {
          address: "",
          status: "ğŸ¦Š Connect to Metamask using the top right button.",
        };
      }
    } catch (err) {
      return {
        address: "",
        status: "ğŸ˜¥ " + err.message,
      };
    }
  } else {
    return {
      address: "",
      status: (
        <span>
          <p>
            {" "}
            ğŸ¦Š{" "}
            <a target="_blank" href={`https://metamask.io/download.html`}>
              You must install Metamask, a virtual Ethereum wallet, in your
              browser.
            </a>
          </p>
        </span>
      ),
    };
  }
};

// ------------------------------


  //the UI of our component
  return (
    <div id="container">

      <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>

      <h2 style={{ paddingTop: "50px" }}>Current Message:</h2>
      <p>{message}</p>

      <h2 style={{ paddingTop: "18px" }}>New Message:</h2>

      <div>
        <input
          type="text"
          placeholder="Update the message in your smart contract."
          onChange={(e) => setNewMessage(e.target.value)}
          value={newMessage}
        />
        <p id="status">{status}</p>

        <button id="publish">
          Update
        </button>
      </div>
    </div>
  );
};



export default HelloWorld;
